mapper.select:
select o.orderId,u.userName,o.payTime,a.phone, concat(a.province,a.city,a.county,a.detail) as address, o.totalPrice,o.`status` from orders_show as o left JOIN user as u on o.userId = u.id left JOIN address as a on o.addressId = a.id
u.userName like CONCAT('%',#{ordersShowVo.userName},'%')
o.orderId = #{ordersShowVo.orderId}
o.payTime between #{orderShowVo.startingTime} and #{orderShowVo.deadline}

select count(*) from orders_show as o left JOIN user as u on o.userId = u.id left JOIN address as a on o.addressId = a.id
u.userName like CONCAT('%',#{ordersShowVo.userName},'%')
o.orderId = #{ordersShowVo.orderId}
o.payTime between #{orderShowVo.startingTime} and #{orderShowVo.deadline}
